package revision;

public class revisionday4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//relational operator
		//<,>,<=,>=,==
		
		//boolean value
		
		System.out.println(10>12);
		System.out.println(10<=12);
		System.out.println(10>=12);
		System.out.println(10<=12);
		System.out.println(10==12);
		
		//logical operators
		
		//and
		System.out.println (15>12 && 10<12);
		System.out.println (10>=12 && 15<=12);
		System.out.println (10<12 && 10>12);
		System.out.println (10<=12 && 10>=12);
		System.out.println (10==12 && 10==12);
		
		//OR
		
		System.out.println (15>12  || 10<12);
		System.out.println (10>=12 || 15<=12);
		System.out.println (10<12  || 10>12);
		System.out.println (10<=12 || 10>=12);
		System.out.println (10==12 || 10==12);
		
		//Not !
		System.out.println (!(15>12));
		System.out.println (!(10>=12 ));
		System.out.println (!(10<12 ));
		System.out.println (!(10<=12 ));
		System.out.println (!(10==12));
		
	}

}
